'use strict';

//Reservations invoice items service used to communicate Reservation's invoice items REST endpoints
angular.module('reservations').factory('ReservationsInvoiceItems', [
    '$resource',
    'AppConfig',
    function($resource, AppConfig) {

        var baseApi2Url = AppConfig.API_URL;
        var baseApiOldUrl = AppConfig.API_OLD_URL;

        return $resource(baseApiOldUrl + 'reservations/:reservationId/invoice-items/:invoiceItemId', {
            invoiceItemId: '@_id',
            reservationId: '@reservationId'
        }, {
            update: {
                method: 'PUT'
            }
        });
    }
]);
