'use strict';

//Setting up route
angular.module('reviews').config(['$stateProvider',
	function($stateProvider) {
		// Reviews state routing
		$stateProvider.
		state('reviews', {
			abstract: true,
			url: '/reviews',
			template: '<ui-view/>'
		}).
		state('reviews.list', {
			url: '',
			title: 'Reviews',
			templateUrl: 'app/reviews/views/list-reviews.client.view.html'
		}).
		state('reviews.create', {
			url: '/create',
			title: 'Review create',
			templateUrl: 'app/reviews/views/create-review.client.view.html'
		}).
		state('reviews.view', {
			url: '/:reviewId',
			title: 'Review view',
			templateUrl: 'app/reviews/views/view-review.client.view.html'
		}).
		state('reviews.edit', {
			url: '/:reviewId/edit',
			title: 'Review edit',
			templateUrl: 'app/reviews/views/edit-review.client.view.html'
		});
	}
]);
