'use strict';

// Reviews controller
angular.module('reviews').controller('ReviewsController', ['$scope', '$stateParams', '$location', 'Authentication', 'Reviews', 'notify',
	function($scope, $stateParams, $location, Authentication, Reviews, notify) {
		$scope.authentication = Authentication;

		// Create new Review
		$scope.create = function(data) {
			// Create new Review object
			data.category = $scope.reviewsCategory;
			var review = new Reviews (data);

			// Redirect after save
			review.$save(function() {
				notify({message: 'Review saved successfully', classes: 'alert-success'});
				$scope.find();
			}, function(errorResponse) {
				notify({message: 'Review save failed', classes: 'alert-danger'});
				$scope.error = errorResponse.data.message;
			});
		};

		// Remove existing Review
		$scope.remove = function( review ) {
			review.$remove();
			for (var i in $scope.reviews ) {
				if ($scope.reviews [i] === review ) {
					$scope.reviews.splice(i, 1);
				}
			}
		};

		// Update existing Review
		$scope.update = function(review) {
			review.$update(function() {
				notify({message: 'Review saved successfully', classes: 'alert-success'});
			}, function(errorResponse) {
				notify({message: 'Review save failed', classes: 'alert-danger'});
				$scope.error = errorResponse.data.message;
			});
		};

		// Find a list of Reviews
		$scope.find = function() {
			$scope.reviews = Reviews.query({
				category: $scope.reviewsCategory
			});
		};

		// Find existing Review
		$scope.findOne = function() {
			$scope.review = Reviews.get({
				reviewId: $stateParams.reviewId
			});
		};

		$scope.addTemplate = function(){
            $scope.create({title:'New Template'});
        };

        $scope.addDivider = function(){
            $scope.create({divider: true});
        };

		$scope.toggleReview = function(review){
            review.visible = !review.visible;
        };
	}
]);
