'use strict';

// Configuring the Systems module
angular.module('system').run(['Menus',
	function(Menus) {
		// Add the System's dropdown item
		Menus.addMenuItem('topbar', {
			title: 'System',
			state: 'system',
			type: 'dropdown',
			roles: ['system-manager', 'admin'],
			position: 5
		});

		Menus.addSubMenuItem('topbar', 'system', {
			title: 'Hooks',
			state: 'system.automation'
		});

		Menus.addSubMenuItem('topbar', 'system', {
			title: 'Public Reviews',
			state: 'system.reviews.public'
		});

		Menus.addSubMenuItem('topbar', 'system', {
			title: 'Saved replies',
			state: 'system.cannedResponses'
		});

		// Add the System's main menu
		Menus.addMenu('system', {
			items: [
                {
                    title: 'Hooks',
                    state: 'system.automation',
                    icon: 'glyphicon glyphicon-tasks'
                },
                {
                    title: 'Public Reviews',
                    state: 'system.reviews.public',
                    icon: 'glyphicon glyphicon-eye-open'
                },
                {
                    title: 'Saved replies',
                    state: 'system.cannedResponses',
                    icon: 'fa fa-floppy-o'
                }
            ]
		});
	}
]);
