'use strict';

//Setting up route
angular.module('system').config(['$stateProvider',
	function($stateProvider) {

		$stateProvider
            .state('system', {
				abstract: true,
				url: '/system',
				controller: 'SystemController',
				templateUrl: 'app/core/views/sidebar.client.view.html'
			})
            .state('system.automation', {
				url: '/hooks',
				title: 'Hooks',
				templateUrl: 'app/system/views/system-automation.client.view.html'
			})
            .state('system.cannedResponses', {
				url: '/canned-responses',
				title: 'Saved replies',
				templateUrl: 'app/system/views/system-canned-responses.client.view.html'
			})
            .state('system.reviews', {
				abstract: true,
				url: '/reviews',
				template: '<ui-view/>'
			})
            .state('system.reviews.public', {
				url: '/public',
				title: 'Public Reviews',
				templateUrl: 'app/system/views/system-public-reviews.client.view.html'
			})
        ;
	}
]);
