'use strict';

// System controller
angular.module('system').controller('SystemController', ['$scope', '$stateParams', '$location', '$uibModal', 'Menus', 'Authentication',
    function($scope, $stateParams, $location, $uibModal, Menus, Authentication) {
        // Set sidebar details
        $scope.sidebar = Menus.getMenu('system');
        $scope.sidebarTitle = 'System';

        $scope.authentication = Authentication;

        // open log modal
        $scope.openLogModal = function(category) {
            $uibModal.open({
                size: 'lg',
                windowClass: 'log-modal',
                templateUrl: 'app/accounts/views/modals/log.client.view.html',
                controller: 'LogModalController',
                resolve: {
                    formData: function() {
                        return {
                            category: category
                        };
                    }
                }
            });
        };
    }
]);
