'use strict';

//Setting up route
angular.module('team').config(['$stateProvider',
	function($stateProvider) {

		$stateProvider.
			state('team', {
				abstract: true,
				url: '/team',
				template: '<ui-view/>'
			}).
			state('team.updates', {
				url: '/updates/:mode',
				params: {
					mode: 'unseen'
				},
				templateUrl: 'app/team/views/team-updates.client.view.html'
			}).
			state('team.updates-post', {
				url: '/update/:postId',
				templateUrl: 'app/team/views/team-updates.client.view.html'
			});
	}
]);
