'use strict';

//Shifts service used to communicate Shifts REST endpoints
angular.module('team').factory('TeamUpdatePost', [
    '$resource',
    'AppConfig',
    function($resource, AppConfig) {

        var baseApi2Url = AppConfig.API_URL;
        var baseApiOldUrl = AppConfig.API_OLD_URL;

        return $resource(baseApiOldUrl + 'team/updates/:_id', {_id:'@_id'}, {
            update: {
                method: 'PUT'
            },
            addToList: {
                url: baseApiOldUrl + 'team/updates/:_id/add-employee',
                method: 'POST'
            }
        });
    }
]);
