'use strict';

//Setting up route
angular.module('templates').config(['$stateProvider',
    function($stateProvider) {
        // Inbox state routing
        $stateProvider.
        state('cannedResponses', {
            url: '/canned-responses',
            abstract: true,
            template: '<ui-view/>'
        }).
        state('cannedResponses.list', {
            url: '/list',
            templateUrl: 'app/templates/views/canned-responses/list-canned-responses.client.view.html'
        }).
        state('cannedResponses.template', {
            url: '/:cannedResponseId',
            templateUrl: 'app/templates/views/canned-responses/canned-response.client.view.html'
        });
    }
]);
