'use strict';

angular.module('templates').factory('CannedResponsesModals', ['$uibModal',
	function($uibModal) {
        // Public API
        return {
            open: function(id, opts) {
                return $uibModal.open({
                    templateUrl: 'app/templates/views/canned-responses/canned-response.client.view.html',
                    size: 'lg',
                    windowClass: 'canned-response-modal',
                    controller: function($scope, $uibModalInstance) {
                        $scope.cannedResponseId = id;
                        $scope.queryParams = opts;
                        $scope.cannedResponseModal = $uibModalInstance;
                    }
                }).result;
            }
        };
	}
]);
