'use strict';

//Todos service used to communicate Todos REST endpoints
angular.module('todos').factory('Todos', [
    '$resource',
    'AppConfig',
	function($resource, AppConfig) {

        var baseApi2Url = AppConfig.API_URL;
        var baseApiOldUrl = AppConfig.API_OLD_URL;

		return $resource(baseApiOldUrl + 'todos/:todoId', { todoId: '@_id'
		}, {
			update: {
				method: 'PUT'
			}
		});
	}
]);